import introLine from "../img/introLine.png";
import introB from "../img/introbottom.png";
import introArrow from "../img/introarrow.png";

export default function Intro() {
  function introOpen() {
    const introBox = document.querySelector(".intro_box");
    const introBottom = document.querySelector(".intro_bottom");
    introBox.classList.add("intro_box_open");
    introBottom.classList.add("intro_bottom_open");
  }

  return (
    <>
      <div className="intro_box">
        <div className="intro_line">
          <img src={introLine} />
          <div className="intro_here">
            <img src={introArrow} />
            <span>Click</span>
          </div>
          <div className="intro_button" onClick={introOpen}></div>
        </div>
      </div>

      <div className="intro_bottom">
        <img src={introB} />
      </div>
    </>
  );
}
