import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const [baseUrl, setUrl] = useState("https://pokeapi.co/api/v2/pokemon");
  const [getPo, setPo] = useState([]);

  const axiosUrl = async () => {
    const dats = await axios.get(baseUrl);
    setData(dats.data.results);
  };

  const getPoke = async () => {
    data.map(async (item) => {
      const ios = await axios.get(item.url);
      setPo(ios.data);
    });
  };

  useEffect(() => {
    axiosUrl();
    getPoke();
  }, [baseUrl]);

  return (
    <div className="App">
      {getPo.map((res) => {
        return <p>{res.name}</p>;
      })}
    </div>
  );
}

export default App;
