import "./style.css";
import logo from "./img/logo.png";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [pokeData, setpokeData] = useState([]);
  const baseUrl = "https://pokeapi.co/api/v2/pokemon";

  const lolApi = async () => {
    let basedata = await axios.get(baseUrl);
    getData(basedata.data.results);
  };

  function getData(res) {
    res.map(async (gep) => {
      const gel = await axios.get(gep.url);
      setpokeData((state) => {
        state = [...state, gel.data];
        return state.sort((a, b) => {
          return a.id - b.id;
        });
      });
    });
  }

  useEffect(() => {
    lolApi();
  }, []);

  /*       {poData.map((res, key) => {
        return (
          <div key={key} className="teBox">
            <img src={res.sprites.other.dream_world.front_default}></img>
            <p>{res.id}</p>
            <span>이름 : {res.name}</span>
            <p>무게 : {res.weight} </p>
            <p>크기 : {res.height} </p>
            <pre>
              속성 : {res.types[0].type.name} ,
              {res.types[1] && res.types[1].type.name}
            </pre>
          </div>
        );
      })} */

  return (
    <article className="main">
      <div className="content_box">
        <div className="side_menu">
          <h1 className="logo">
            <img src={logo} />
          </h1>
          <ul>
            <li>A123</li>
            <li>B123</li>
            <li>C123</li>
          </ul>
        </div>
        <div className="items">
          <h2>PokeMon Encyclopedia Web</h2>
          <ul className="item_list">
            {pokeData.map((res, key) => {
              return (
                <li key={res.id}>
                  <div>{res.id}</div>
                  <img src={res.sprites.other.dream_world.front_default}></img>
                  <section className="text_box">
                    <h3></h3>
                    <div className="elo"> {res.types[0].type.name}</div>
                  </section>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </article>
  );
}

export default App;
