import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const baseUrl = "https://pokeapi.co/api/v2/pokemon";
  const [getPo, setPo] = useState([]);

  const axiosUrl = async () => {
    const dats = await axios.get(baseUrl);
    getPoke(dats.data.results);
  };

  const getPoke = async (res) => {
    res.map(async (item) => {
      const ios = await axios.get(item.url);
      setPo((state) => {
        state = [...state, ios.data];
        return state;
      });
    });
  };

  useEffect(() => {
    axiosUrl();
  }, []);

  return <div className="App"></div>;
}

export default App;
