import { useState } from "react";
import seachicon from "../img/seachicon.svg";
import Order from "./Order";
import PageBtn from "./PageBtn";
import TypeBox from "./TypeBox";

export default function Home({
  searched,
  pageOffset,
  itemCount,
  getType,
  getValue,
  useInput,
  page,
  numPage,
  setPage,
  getOrder,
  typeShow,
}) {
  let co;

