import "./style.css";
import logo from "./img/logo.png";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [poData, setpoData] = useState([]); /* 포켓몬 기본 데이터 배열 */
  const [itemCount, setitemCount] = useState(15); /* 표시할 아이템 수 */
  const [page, setPage] = useState(1); /* 페이지 */
  const pageOffset = (page - 1) * itemCount; /* 페이지 오프셋  */
  const [asd, setasd] = useState([]);

  const [useInput, setInput] = useState(""); /* 검색 input value */
  const baseUrl = "https://pokeapi.co/api/v2/pokemon?offset=0&limit=151";

  const [searched, setSear] = useState([]);

  const numPage = Math.ceil(searched.length / itemCount); /* 페이지 버튼 수 */

  const getValue = (e) => {
    setInput(e.target.value.toLowerCase());
  }; /* 검색 input Value 값 */

  const pokeApi = async () => {
    let data = await axios.get(baseUrl);
    getData(data.data.results);
  }; /* 포켓몬 api 기본 */

  function getData(res) {
    res.map(async (gep) => {
      const gel = await axios.get(gep.url);
      setpoData((state) => {
        state = [...state, gel.data];
        return state.sort((a, b) => {
          return a.id - b.id;
        });
      });
    });
  } /* 포켓몬 api 상세정보 */

  function getType(res) {
    if (res == "전체") {
      setSear(poData);
    } else {
      setSear(poData.filter((a) => a.types[0].type.name === res));
    }
  }

  return (
    <article className="main">
      <div className="content_box">
        <div className="side_menu">
          <h1 className="logo">
            <img src={logo} />
          </h1>
          <ul>
            <li>A123</li>
            <li>B123</li>
            <li>C123</li>
          </ul>
        </div>
        <div className="items">
          <h2>PokeMon Encyclopedia Web</h2>
          <ul className="item_list">
            {searched.slice(offset, offset + itemCount).map((res, key) => {
              if (res.types[0].type.name === "fighting") {
                return (
                  <li key={res.id}>
                    <div>{res.id}</div>
                    <img
                      src={res.sprites.other.dream_world.front_default}
                    ></img>
                    <section className="text_box">
                      <h3>{res.name}</h3>
                      <div className="elo">
                        {" "}
                        <span>{res.types[0].type.name}</span>
                      </div>
                    </section>
                  </li>
                );
              }
            })}
          </ul>
          <nav>
            <button onClick={() => setPage(page - 1)} disabled={page === 1}>
              &lt;
            </button>
            {Array(numPages)
              .fill()
              .map((v, i) => (
                <button
                  key={i + 1}
                  onClick={() => setPage(i + 1)}
                  aria-current={page === i + 1 ? "page" : null}
                >
                  {i + 1}
                </button>
              ))}
            <button
              onClick={() => setPage(page + 1)}
              disabled={page === numPages}
            >
              &gt;
            </button>
          </nav>
        </div>
      </div>
    </article>
  );
}

export default App;
