import "./style.css";
import logo from "./img/logo.png";
import pic from "./img/p1.png";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [baseUrl, setbaseUrl] = useState(
    "https://pokeapi.co/api/v2/pokemon-species?offset=0&limit=15"
  );
  const [testUrl, settestUrl] = useState(
    "https://pokeapi.co/api/v2/pokemon?offset=0&limit=15"
  );
  const [getPo, setPo] = useState({});

  const axiosUrl = async () => {
    const base = await axios.get(baseUrl);
    const test = await axios.get(testUrl);
    getPoke(base.data.results, test.data.results);
  };

  const getPoke = async (res, els) => {
    res.map(async (item) => {
      const ios = await axios.get(item.url);
      setPo((state) => {
        state = [...state, ios.data.names[2].name];
        return state;
      });
    });
    els.map(async (els) => {
      const tos = await axios.get(els.url);
      setPo((state) => {
        state = [...state, tos.data];
        return state;
      });
    });
  };

  console.log(getPo);
  useEffect(() => {
    axiosUrl();
  }, [baseUrl]);

  return (
    <article className="main">
      <div className="content_box">
        <div className="side_menu">
          <h1 className="logo">
            <img src={logo} />
          </h1>
          <ul>
            <li>A123</li>
            <li>B123</li>
            <li>C123</li>
          </ul>
        </div>
        <div className="items">
          <h2>PokeMon Encyclopedia Web</h2>
          <ul className="item_list">
            {getPo.map((res) => {
              return (
                <li>
                  <div>{res.id}</div>
                  <img></img>
                  <section className="text_box">
                    <h3>{res.name}</h3>
                    <div className="elo">elo</div>
                  </section>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </article>
  );
}

export default App;
