export default function Modal({ name, modalVi, setmodalVi, searched, id }) {
  const onCloseHandler = () => {
    setmodalVi("");
  };

  return (
    <div
      className="modals"
      style={modalVi === id ? { display: "block" } : { display: "none" }}
      onClick={onCloseHandler}
    >
      <div className="modal_item">{name}</div>
    </div>
  );
}
