export default function Home({ searched, pageOffset, itemCount }) {
  return (
    <div className="items">
      <h2>PokeMon Encyclopedia Web</h2>
      <ul className="item_list">
        {searched.slice(pageOffset, pageOffset + itemCount).map((res, key) => {
          if (res.types[0].type.name === "fighting") {
            return (
              <li key={res.id}>
                <div>{res.id}</div>
                <img src={res.sprites.other.dream_world.front_default}></img>
                <section className="text_box">
                  <h3>{res.name}</h3>
                  <div className="elo">
                    <span>{res.types[0].type.name}</span>
                  </div>
                </section>
              </li>
            );
          }
        })}
      </ul>
      <div className="type_box"></div>
    </div>
  );
}
