export default function Modal({ name, modalVi, setmodalVi, searched, id }) {
  const onCloseHandler = () => {
    setmodalVi("");
  };

  return (
    <>
      <div
        className="modals"
        style={modalVi === id ? { display: "block" } : { display: "none" }}
        onClick={onCloseHandler}
      ></div>
      <div
        className="modal_item"
        style={modalVi === id ? { display: "block" } : { display: "none" }}
      >
        <div className="blackLine"></div>
        <div className="redLine"></div>
        <div className="modal_content">
          <ul>
            <li className="modal_left_textBox">
              <div className="modal_id">03</div>
              <span>metapod</span>
              <span>Encyclopedia</span>
            </li>
            <li className="modal_right_img"></li>
          </ul>
        </div>
      </div>
    </>
  );
}
