import "./style.css";
import logo from "./img/logo.png";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [pokeData, setpokeData] = useState([]);
  const baseUrl = "https://pokeapi.co/api/v2/pokemon/?offset=0&limit=151";
  const [limit, setLimit] = useState(15);
  const [page, setPage] = useState(1);
  const offset = (page - 1) * limit;

  const lolApi = async () => {
    let basedata = await axios.get(baseUrl);
    getData(basedata.data.results);
  };

  function getData(res) {
    res.map(async (gep) => {
      const gel = await axios.get(gep.url);
      setpokeData((state) => {
        state = [...state, gel.data];
        return state.sort((a, b) => {
          return a.id - b.id;
        });
      });
    });
  }

  useEffect(() => {
    lolApi();
  }, []);

  const numPages = Math.ceil(
    pokeData.filter((res) => res.types[0].type.name == "grass").length / limit
  );
  console.log(
    pokeData.filter((res) => res.types[0].type.name == "grass").length
  );

  return (
    <article className="main">
      <div className="content_box">
        <div className="side_menu">
          <h1 className="logo">
            <img src={logo} />
          </h1>
          <ul>
            <li>A123</li>
            <li>B123</li>
            <li>C123</li>
          </ul>
        </div>
        <div className="items">
          <h2>PokeMon Encyclopedia Web</h2>
          <ul className="item_list">
            {pokeData.slice(offset, offset + 151).map((res, key) => {
              if (res.types[0].type.name == "grass") {
                return (
                  <li key={res.id}>
                    <div>{res.id}</div>
                    <img
                      src={res.sprites.other.dream_world.front_default}
                    ></img>
                    <section className="text_box">
                      <h3>{res.name}</h3>
                      <div className="elo">
                        {" "}
                        <span>{res.types[0].type.name}</span>
                      </div>
                    </section>
                  </li>
                );
              }
            })}
          </ul>
          <nav>
            <button onClick={() => setPage(page - 1)} disabled={page === 1}>
              &lt;
            </button>
            {Array(numPages)
              .fill()
              .map((v, i) => (
                <button
                  key={i + 1}
                  onClick={() => setPage(i + 1)}
                  aria-current={page === i + 1 ? "page" : null}
                >
                  {i + 1}
                </button>
              ))}
            <button
              onClick={() => setPage(page + 1)}
              disabled={page === numPages}
            >
              &gt;
            </button>
          </nav>
        </div>
      </div>
    </article>
  );
}

export default App;
