import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const [baseUrl, setUrl] = useState("https://pokeapi.co/api/v2/pokemon");
  const [getPo, setPo] = useState([]);

  const axiosUrl = async () => {
    const dats = await axios.get(baseUrl);
    getPoke(dats.data.results);
  };

  const getPoke = async (res) => {
    res.map(async (item) => {
      const ios = await axios.get(item.url);

      setData((state) => {
        state = [...state, ios.data];
        return state;
      });
    });
  };

  useEffect(() => {
    axiosUrl();
    /* getPoke(); */
  }, [baseUrl]);

  return (
    <div className="App">
      {data.map((res) => {
        return <pre>{res.name}</pre>;
      })}
    </div>
  );
}

export default App;
