import "./style.css";
import logo from "./img/logo.png";
import pic from "./img/p1.png";
import { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState([]);
  const baseUrl = "https://pokeapi.co/api/v2/pokemon";
  const [getPo, setPo] = useState([]);

  const axiosUrl = async () => {
    const dats = await axios.get(baseUrl);
    getPoke(dats.data.results);
  };

  const getPoke = async (res) => {
    res.map(async (item) => {
      const ios = await axios.get(item.url);
      setPo((state) => {
        state = [...state, ios.data];
        return state;
      });
    });
  };

  useEffect(() => {
    axiosUrl();
  }, []);

  console.log(getPo[0].types[0].name);

  return (
    <article className="main">
      <div className="content_box">
        <div className="side_menu">
          <h1 className="logo">
            <img src={logo} />
          </h1>
          <ul>
            <li>A123</li>
            <li>B123</li>
            <li>C123</li>
          </ul>
        </div>
        <div className="items">
          <h2>PokeMon Encyclopedia Web</h2>
          <ul className="item_list">
            {getPo.map((res) => {
              if (res.type[0] === "fairy") {
                return (
                  <li>
                    <div>{res.id}</div>
                    <img src={res.sprites.front_default}></img>
                    <section className="text_box">
                      <h3>{res.name}</h3>
                      <div className="elo">elo</div>
                      <div className="elo">elo</div>
                    </section>
                  </li>
                );
              }
            })}
          </ul>
        </div>
      </div>
    </article>
  );
}

export default App;
