import "./App.css";
import { useState, useEffect } from "react";
import axios from "axios";

const baseUrl = "https://pokeapi.co/api/v2/pokemon";

function App() {
  const [data, setData] = useState([]);

  const axiosUrl = async () => {
    const dats = await axios.get(baseUrl + "/");
    setData(dats.data.results);
  };

  const getPoke = async () => {
    data.map(async (item) => {
      const ios = await axios.get(item.url);

      setData((state) => {
        state = [...state.ios.data];
        return state;
      });
    });
  };

  console.log(data);

  useEffect(() => {
    axiosUrl();
    getPoke();
  }, []);

  return (
    <div className="App">
      {/* {data.map((res) => {
        return <pre key={res.id}>{res.name}</pre>;
      })} */}
    </div>
  );
}

export default App;
